    private void loginButtonLActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_loginButtonLActionPerformed
        if(textFieldIsEmpty()){
           setAdviceLabel("Ingrese datos para el acceso");
        }
        else{
            if(!userAccess()){
                setAdviceLabel("Usuario/Contraseña incorrecta");
            }
            else{
                setAdviceLabel("Acceso permitido");
                dispose();
                functions functionsView = new functions(userTextField.getText());
                userTextField.setText("");
                passTextField.setText("");
                functionsView.setVisible(true);
            }
        }
        
    }//GEN-LAST:event_loginButtonLActionPerformed

