    private boolean textFieldIsEmpty(){
        return usernameIsEmpty()&& passwordIsEmpty();
    }
    
    private boolean usernameIsEmpty(){
        return userTextField.getText().trim().isEmpty();
    }
    private boolean passwordIsEmpty(){
        return userTextField.getText().trim().isEmpty();
    }
    
    private boolean userAccess(){
        String username = userTextField.getText().trim();
        String password = passTextField.getText();
        return username.equals("admin") && password.equals("1234");
    }
    
    private void setAdviceLabel(String message){
        adviceLabelL.setText(message);
    }
    
